class Solution4 {

    fun solve(input: String): Long {
        var x: Int
        var y = 0

        val lines = input.split('\n')

        val xSize = lines.first().length
        val ySize = lines.size

        val matrix = Array(xSize) { CharArray(ySize) }
        lines.forEach { line ->
            x = 0
            line.toCharArray().forEach {
                matrix[x][y] = it
                x++
            }
            y++
        }

        var solution = 0L

        matrix.forEachIndexed { xi, x ->
            x.forEachIndexed { yi, y ->
                var adjacentCount = 0
                if (y == '@') {
                    //x - 1
                    if (xi - 1 >= 0) {
                        if (yi - 1 >= 0) {
                            if (matrix[xi-1][yi-1] == '@') adjacentCount++
                        }
                        if (matrix[xi-1][yi] == '@') adjacentCount++
                        if (yi + 1 < ySize) {
                            if (matrix[xi-1][yi+1] == '@') adjacentCount++
                        }
                    }
                    // x + 1
                    if (xi + 1 < xSize) {
                        if (yi - 1 >= 0) {
                            if (matrix[xi+1][yi-1] == '@') adjacentCount++
                        }
                        if (matrix[xi+1][yi] == '@') adjacentCount++
                        if (yi + 1 < ySize) {
                            if (matrix[xi+1][yi+1] == '@') adjacentCount++
                        }
                    }
                    if (yi - 1 >= 0) {
                        if (matrix[xi][yi-1] == '@') adjacentCount++
                    }
                    if (yi + 1 < ySize) {
                        if (matrix[xi][yi+1] == '@') adjacentCount++
                    }
                    if (adjacentCount < 4) {
                        solution++
                    }
                }
            }
        }
        return solution
    }

}